import React, { useState } from 'react';

interface DreamFormProps {
  onSubmit: (description: string) => void;
  onClose: () => void;
  isLoading: boolean;
}

const DreamForm: React.FC<DreamFormProps> = ({ onSubmit, onClose, isLoading }) => {
  const [description, setDescription] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (description.trim() && !isLoading) {
      onSubmit(description.trim());
    }
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-gradient-to-br from-dream-purple to-dream-blue border border-white/20 rounded-2xl p-6 w-full max-w-md">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-dream-gold/20 rounded-full flex items-center justify-center">
              <span className="text-dream-gold text-xl">🌙</span>
            </div>
            <div>
              <h2 className="text-xl font-bold text-white">Share Your Dream</h2>
              <p className="text-sm text-white/70">Let your dream become a star</p>
            </div>
          </div>
          <button
            onClick={onClose}
            className="text-white/70 hover:text-white transition-colors"
          >
            ✕
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-white/80 mb-2">
              What did you dream about?
            </label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Describe your dream in detail... What did you see, feel, or experience? The more details you provide, the more magical your dream star will be."
              className="w-full h-32 px-4 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-white/50 resize-none focus:outline-none focus:ring-2 focus:ring-dream-gold/50 focus:border-dream-gold/50 transition-all"
              disabled={isLoading}
            />
          </div>

          {/* Tips */}
          <div className="bg-white/5 rounded-lg p-3">
            <div className="flex items-start gap-2">
              <span className="text-dream-gold text-sm">✨</span>
              <div className="text-xs text-white/70">
                <p className="font-medium text-white/80 mb-1">Tips for better dream visualization:</p>
                <ul className="space-y-1">
                  <li>• Include colors, emotions, and atmosphere</li>
                  <li>• Describe any people, places, or objects</li>
                  <li>• Mention the overall feeling or mood</li>
                </ul>
              </div>
            </div>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            disabled={!description.trim() || isLoading}
            className="w-full bg-gradient-to-r from-dream-gold to-yellow-400 text-dream-blue font-semibold py-3 px-6 rounded-lg hover:from-yellow-400 hover:to-dream-gold transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
          >
            {isLoading ? (
              <>
                <div className="w-4 h-4 border-2 border-dream-blue/30 border-t-dream-blue rounded-full animate-spin" />
                Processing Dream...
              </>
            ) : (
              <>
                <span>📤</span>
                Create Dream Star
              </>
            )}
          </button>
        </form>

        {/* Character count */}
        <div className="mt-3 text-right">
          <span className={`text-xs ${description.length > 500 ? 'text-red-400' : 'text-white/50'}`}>
            {description.length}/500
          </span>
        </div>
      </div>
    </div>
  );
};

export default DreamForm;
